import React, { useState } from "react";
import "./ExpensesFilter.css";
import Card from "../UI/Card";
import ExpenseBar from "../UI/ExpenseBar";

function ExpensesFilter() {
  const dropdownChangeHandler = (e) => {
    console.log(e.target.value);
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={dropdownChangeHandler}>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
      <Card className="expense-by-month">
        <ExpenseBar month="Jan" />
        <ExpenseBar month="Feb" />
        <ExpenseBar month="Mar" />
        <ExpenseBar month="Apr" />
        <ExpenseBar month="May" />
        <ExpenseBar month="Jun" />
        <ExpenseBar month="Jul" />
        <ExpenseBar month="Aug" />
        <ExpenseBar month="Sep" />
        <ExpenseBar month="Oct" />
        <ExpenseBar month="Nov" />
        <ExpenseBar month="Dec" />
      </Card>
    </div>
  );
}

export default ExpensesFilter;
